import { LeftArrowIcon, SearchIcon } from 'assets/svgs'
import { FormEvent } from 'react'
import styles from './searchBook.module.scss'

const SearchBook = () => {
  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault()
    setSearchOpen(false)
    navigate(`searchresult/${input}`)
    setInput('')
    let searchStoreArray = store.get('searchStore')
    const tmpArray = searchStoreArray.filter((item: string) => item !== input)
    searchStoreArray = [...tmpArray, input]
    store.set('searchStore', searchStoreArray)
  }
  return (
    <div>
      <form className={styles.searchBook} onSubmit={handleSubmit}>
        <button type='button' onClick={handleSearchClick}>
          <LeftArrowIcon className={styles.icon} />
        </button>
        <input
          type='text'
          placeholder='검색어를 입력하세요.'
          onChange={handleChange}
          value={input}
          onClick={handleClickInput}
        />
        <SearchIcon className={styles.icon} />
      </form>
    </div>
  )
}

export default SearchBook
