import cx from 'classnames'
import { useEffect, useState } from 'react'
import styles from './modal.module.scss'

interface Props {
  contents: string
  isShow: boolean
  name: string
}

const Modal = ({ contents, isShow, name }: Props) => {
  const [modalOpen, setModalOpen] = useState(isShow)

  const handleClick = () => {
    setModalOpen((prev) => !prev)
  }

  useEffect(() => setModalOpen(isShow), [isShow])

  return (
    <div className={cx(styles.modalDiv, { [styles.isShow]: modalOpen })}>
      <div className={cx(styles.modal)}>
        <p>{contents}</p>
        <div className={styles.buttonWrapper}>
          <button type='button' className={styles.cancelBtn} onClick={handleClick}>
            취소
          </button>
          <button type='button' className={styles.okBtn} onClick={handleClick}>
            {name}
          </button>
        </div>
      </div>
    </div>
  )
}

export default Modal
