import { SearchIcon } from 'assets/svgs'
import { debounce } from 'lodash'
import { ChangeEvent, FormEvent, useMemo, useState } from 'react'
import { useQuery } from 'react-query'
import { getSearchListApi } from 'services/bookSearchApi'
import styles from './searchBook.module.scss'

const SearchBook = () => {
  const [input, setInput] = useState('')
  const [search, setSearch] = useState('')

  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault()
  }

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { value } = e.currentTarget
    setInput(value)
    debounceSearch(value)
  }

  const debounceSearch = useMemo(
    () =>
      debounce((value) => {
        setSearch(value)
      }, 1000),
    [setSearch]
  )

  const { data } = useQuery(
    ['getSearchListApi', search, input],
    () => getSearchListApi(search, 1).then((res) => res.data),
    {
      enabled: !!search,
    }
  )

  return (
    <div>
      <form className={styles.searchBook} onSubmit={handleSubmit}>
        <input type='text' placeholder='검색어를 입력하세요.' onChange={handleChange} value={input} />
        <SearchIcon className={styles.icon} />
      </form>
    </div>
  )
}

export default SearchBook
