import styles from './main.module.scss'
import Carousel from './Carousel'
import { RowBookList } from 'components/RowBookList'
import { bestSeller } from 'data/bestSeller'
import SearchForm from 'components/SearchForm'
import GNB from 'routes/_shared/GNB'
import cx from 'classnames'
import { useState } from 'react'
import { getSearchListApi } from 'services/bookSearchApi'
import { useQuery } from 'react-query'
import { useRecoilValue } from 'recoil'
import { inputValue } from 'states/inputValue'
import { Link } from 'react-router-dom'
import store from 'store'

interface SearchStructure {
  authors: string[]
  contents: string
  datetime: string
  isbn: string
  price: number
  publisher: string
  sale_price: number
  status: string
  thumbnail: string
  title: string
  translators: any[]
  url: string
}

function GetSearch(searchValue: string) {
  console.log(searchValue)
  const { data, isLoading } = useQuery(
    ['getSearchListApi', searchValue],
    () => getSearchListApi(searchValue).then((res) => res.data),
    {
      enabled: !!searchValue,
    }
  )
  console.log(data)
  return data
}

const Main = () => {
  const [isSearchShow, setIsSearchShow] = useState(false)
  const [searchValue, setSearchValue] = useState('')
  const input = useRecoilValue(inputValue)

  if (!store.get('searchStore')) {
    store.set('searchStore', [])
  }

  const { data } = useQuery(
    ['getSearchListApi', searchValue],
    () => getSearchListApi(searchValue).then((res) => res.data),
    {
      enabled: !!searchValue,
    }
  )

  const test = GetSearch('정세랑')
  console.log(test)
  return (
    <div className={styles.main}>
      <GNB />
      <div className={styles.mainWrapper}>
        <SearchForm popup={false} setIsSearchShow={setIsSearchShow} setSearchValue={setSearchValue} />
        <div>
          <Carousel />
        </div>
        <RowBookList bookList={bestSeller} />
      </div>
      <div className={cx(styles.mobileSearch, { [styles.isShow]: isSearchShow })}>
        <SearchForm popup setIsSearchShow={setIsSearchShow} setSearchValue={setSearchValue} />
        <div className={cx(styles.searchList, styles.isExist)}>
          <p>최근 검색어</p>
          {input && data ? (
            <ul>
              {data.documents.map((item: SearchStructure) => (
                <li key={item.isbn}>
                  <Link style={{ display: 'block', width: '100%' }} to={`bookdetail/${item.publisher} ${item.title}`}>
                    <span className=''>{item.title}</span>
                    <span>{item.authors}</span>
                  </Link>
                </li>
              ))}
            </ul>
          ) : (
            // 수정필요
            <ul>
              <li>최근검색어목록</li>
            </ul>
          )}
        </div>
      </div>
    </div>
  )
}

export default Main
