import ModalPortal from '.'
import styles from './modal.module.scss'
import cx from 'classnames'
import { useRecoil } from 'hooks/state'
import { bookCardValue } from 'states/inputSearchValue'

interface Props {
  children: string
  setOnModal: (state: boolean) => void
  onModal: boolean
  multiple: boolean
}

const ModalFrame = ({ children, setOnModal, onModal, multiple }: Props) => {
  const [, setBookIsbn] = useRecoil(bookCardValue)
  const handleClick = () => {
    setOnModal(false)
    setBookIsbn('')
  }

  return (
    <ModalPortal>
      <div className={cx(styles.modalDim, { [styles.showModal]: onModal })} role='presentation' onClick={handleClick}>
        <div className={styles.modalBox}>
          {multiple ? (
            <div>
              {children}
              <button className='close' type='button' onClick={handleClick}>
                ë„¤
              </button>
            </div>
          ) : (
            <div>
              {children}
              <button className='close' type='button' onClick={handleClick}>
                X
              </button>
            </div>
          )}
        </div>
      </div>
    </ModalPortal>
  )
}

export default ModalFrame
