import styles from './carousel.module.scss'
import cx from 'classnames'
import { useState } from 'react'

const Carousel = () => {
  const [showCard, setShowCard] = useState(1)
  setInterval(() => {
    setShowCard((prev) => (prev + 1 > 3 ? 1 : prev + 1))
  }, 3000)
  return (
    <div>
      <h2 className={styles.test}> Single Item</h2>
      <div className={styles.slider}>
        <ul className={styles.wrapper}>
          <li className={cx(styles.box1, { [styles.isShow]: showCard === 1 })}>1</li>
          <li className={cx(styles.box2, { [styles.isShow]: showCard === 2 })}>2</li>
          <li className={cx(styles.box3, { [styles.isShow]: showCard === 3 })}>3</li>
        </ul>
      </div>
    </div>
  )
}

export default Carousel
