import { atom } from 'recoil'
import store from 'store'

interface SearchStructure {
  authors: string[]
  contents: string
  datetime: string
  isbn: string
  price: number
  publisher: string
  sale_price: number
  status: string
  thumbnail: string
  title: string
  translators: any[]
  url: string
}

const cartStoreData = store.get('cartStore')

export const cartStoreState = atom<Array<SearchStructure>>({
  key: 'cartStoreState',
  default: cartStoreData || [],
})

const likeStoreData = store.get('cartStore')

export const likeStoreState = atom<Array<SearchStructure>>({
  key: 'likeStoreState',
  default: likeStoreData || [],
})
