import styles from './itemList.module.scss'
import { MouseEvent, SetStateAction, useEffect, useState } from 'react'
import { useRecoil } from 'hooks/state'
import { cartStoreState, likeStoreState } from 'states/storeState'
import store from 'store'
import { SearchStructure } from 'types/searchStructure'
import thousandReExp from 'utils/thousandReExp'
import { inputValue, searchValue } from 'states/inputSearchValue'
import cx from 'classnames'
import noImage from 'assets/images/noImage.png'
import { Link } from 'react-router-dom'

interface Props {
  item: SearchStructure
  type: string
  setState: React.Dispatch<SetStateAction<boolean>>
  clickDel: boolean
}

const ItemList = ({ item, type, setState, clickDel }: Props) => {
  const [cartStoreData, setCartStoreData] = useRecoil(cartStoreState)
  const [likeStoreData, setLikeStoreData] = useRecoil(likeStoreState)
  const [, setInput] = useRecoil(inputValue)
  const [, setSearch] = useRecoil(searchValue)
  const [nowType, setNowType] = useState('')
  const [nowValue, setNowValue] = useState<string>('')

  const handleBtnClick = (e: MouseEvent<HTMLButtonElement>) => {
    const { value } = e.currentTarget.dataset
    const { className } = e.currentTarget

    if (value === undefined) return

    if (className.split('_')[1] === 'deleteBtn') {
      setNowType(type)
      setNowValue(value)
    }
    setState(true)
  }

  // const handleClick = () => {
  //   if (nowType === 'cartStore') {
  //     store.set(
  //       nowType,
  //       cartStoreData.filter((data: SearchStructure) => data.isbn !== nowValue)
  //     )
  //     setCartStoreData(store.get(nowType))
  //   } else {
  //     store.set(
  //       'likeStore',
  //       likeStoreData.filter((data: SearchStructure) => data.isbn !== nowValue)
  //     )
  //     setLikeStoreData(store.get(nowType))
  //     setInput('')
  //     setSearch('')
  //   }
  // }

  useEffect(() => {
    if (clickDel) {
      if (nowType === 'cartStore') {
        store.set(
          nowType,
          cartStoreData.filter((data: SearchStructure) => data.isbn !== nowValue)
        )
        setCartStoreData(store.get(nowType))
      } else {
        store.set(
          'likeStore',
          likeStoreData.filter((data: SearchStructure) => data.isbn !== nowValue)
        )
        setLikeStoreData(store.get(nowType))
        setInput('')
        setSearch('')
      }
    }
  }, [
    cartStoreData,
    clickDel,
    likeStoreData,
    nowType,
    nowValue,
    setCartStoreData,
    setInput,
    setLikeStoreData,
    setSearch,
  ])

  return (
    <li className={styles.itemWrapper} key={item.isbn}>
      <div className={styles.item}>
        <Link to={`bookdetail/${item.publisher} ${item.title}`} className={cx(styles.recentItemTitle)}>
          {item.thumbnail === '' ? (
            <img className={styles.image} src={noImage} alt={item.thumbnail} />
          ) : (
            <img className={styles.image} src={item.thumbnail} alt={`${item.title}_img`} />
          )}
        </Link>
        <div className={styles.content}>
          <div className={styles.bookWrap}>
            <div className={styles.bookTitle}>{item.title}</div>
            <div className={styles.bookAuthor}>{item.authors}</div>
            <div className={cx(styles.bookPrice, { [styles.isSoldout]: item.sale_price === -1 })}>
              {thousandReExp(item.sale_price === -1 ? item.price : item.sale_price)}원
            </div>
          </div>
          <div className={styles.buttonWrap}>
            <button type='button' className={styles.deleteBtn} data-value={item.isbn} onClick={handleBtnClick}>
              삭제
            </button>
            <button
              type='button'
              className={cx(styles.buyBtn, { [styles.isSoldout]: item.sale_price === -1 })}
              data-value={item.isbn}
              onClick={handleBtnClick}
            >
              {item.sale_price === -1 ? '품절' : '구매'}
            </button>
          </div>
        </div>
      </div>
      {item.sale_price === -1 && <p className={styles.soldoutMsg}>품절된 상품입니다.</p>}
    </li>
  )
}

export default ItemList
