import styles from './rowBookList.module.scss'
import { Link } from 'react-router-dom'

interface Props {
  bookList: { no: number; authors: string[]; thumbnail: string; title: string }[]
}

export const RowBookList = ({ bookList }: Props) => {
  return (
    <ul className={styles.bookList}>
      {bookList.map((item) => (
        <li key={item.no}>
          <div>
            <Link style={{ display: 'block', width: '100%' }} to={`bookdetail/${item.title}`}>
              <img className='fit-picture' src={item.thumbnail} alt={`${item.title}_img`} />
              <div>{item.title}</div>
              <div>{item.authors}</div>
            </Link>
          </div>
        </li>
      ))}
    </ul>
  )
}
