import ModalPortal from '.'
import styles from './modal.module.scss'
import cx from 'classnames'

interface Props {
  children: string
  setOnModal: (state: boolean) => void
  onModal: boolean
  selectItem: () => void
}

const ModalFrame = ({ children, setOnModal, onModal }: Props) => {
  const handleClick = () => {
    setOnModal(false)
  }
  return (
    <ModalPortal>
      <div className={cx(styles.modalDim, { [styles.showModal]: onModal })} role='presentation' onClick={handleClick}>
        <div className={styles.modalBox}>
          {children}
          <button className='close' type='button' onClick={handleClick}>
            X
          </button>
        </div>
      </div>
    </ModalPortal>
  )
}

export default ModalFrame
