import styles from './searchForm.module.scss'
import { SearchIcon } from 'assets/svgs'
import { ChangeEvent, FormEvent, useEffect, useMemo, useState } from 'react'
import debounce from 'lodash/debounce'
import { getSearchListApi } from 'services/bookSearchApi'
import { useQuery } from 'react-query'

const SearchForm = () => {
  const [inputValue, setInputValue] = useState('')
  const [searchValue, setSearchValue] = useState('')

  const handleInputClick = () => {
    console.log('sdfds')
  }

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { value } = e.currentTarget
    setInputValue(value)
    console.log(value)
    debouncedChangeHandler()
  }
  const { data } = useQuery(
    ['getSearchListApi', inputValue],
    () =>
      getSearchListApi(inputValue).then((res) => {
        console.log(res)
      }),
    {
      enabled: !!inputValue,
      staleTime: 2 * 60 * 1000,
    }
  )

  useEffect(() => {
    console.log(inputValue)
  }, [inputValue]) // const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
  //   e.preventDefault()
  // }

  const debouncedChangeHandler = () =>
    debounce(() => {
      setSearchValue(inputValue)
    }, 1000)

  return (
    <div>
      <form className={styles.form}>
        <div className={styles.searchIcon}>
          <SearchIcon />
        </div>
        <input className={styles.input} onClick={handleInputClick} onChange={debouncedChangeHandler} />
      </form>
      <ul>
        <li>최근 검색어</li>
      </ul>
    </div>
  )
}

export default SearchForm
