import styles from './searchForm.module.scss'
import { SearchIcon } from 'assets/svgs'
import { ChangeEvent, FormEvent, useEffect, useMemo, useState } from 'react'
import { debounce } from 'lodash'
import { getSearchListApi } from 'services/bookSearchApi'
import { useQuery } from 'react-query'

const SearchForm = () => {
  const [inputValue, setInputValue] = useState('')
  const [searchValue, setSearchValue] = useState('')

  const { data } = useQuery(
    ['getSearchListApi', searchValue],
    () =>
      getSearchListApi(searchValue).then((res) => {
        console.log(res)
      }),
    {
      enabled: !!searchValue,
      staleTime: 2 * 60 * 1000,
    }
  )
  const debounceSearch = useMemo(
    () =>
      debounce((input) => {
        setSearchValue(input)
      }, 2000),
    []
  )
  const handleInputClick = () => {
    console.log('sdfds')
  }

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { value } = e.currentTarget
    setInputValue(value)
    // debounceSomethingFunc()
    debounceSearch(value)
  }

  return (
    <div>
      <form className={styles.form}>
        <div className={styles.searchIcon}>
          <SearchIcon />
        </div>
        <input className={styles.input} onClick={handleInputClick} onChange={handleChange} value={inputValue} />
      </form>
      <ul>
        <li>최근 검색어</li>
      </ul>
    </div>
  )
}

export default SearchForm
