import styles from './rowBookList.module.scss'
import { Link } from 'react-router-dom'
import { useRef, useState } from 'react'

interface Props {
  bookList: { no: number; authors: string[]; thumbnail: string; title: string; isbn: string; publisher: string }[]
}

export const RowBookList = ({ bookList }: Props) => {
  const scrollNextRef = useRef<HTMLLIElement>(null)
  const scrollPrevRef = useRef<HTMLLIElement>(null)
  const testRefs = useRef<HTMLLIElement[]>([])
  const [num, setNum] = useState(0)
  const onNextClick = () => {
    scrollNextRef.current?.scrollIntoView({ behavior: 'smooth' })
  }
  const onPrevClick = () => {
    scrollPrevRef.current?.scrollIntoView({ behavior: 'smooth' })
  }

  const test = () => {
    console.log(testRefs)
    testRefs.current[7]?.scrollIntoView({ behavior: 'smooth' })
  }

  return (
    <div>
      <ul className={styles.bookList}>
        {bookList.map((item, idx) => (
          <li
            className={styles.bookItem}
            key={item.no}
            data-value={idx}
            ref={(el) => {
              testRefs.current[idx] === el
              return testRefs
            }}
          >
            <Link to={`detail/${item.publisher} ${item.title}`} className={styles.item}>
              <img className={styles.image} src={item.thumbnail} alt={`${item.title}_img`} />
              <div className={styles.bookWrapper}>
                <div className={styles.number}>{item.no}</div>
                <div className={styles.explain}>
                  <div className={styles.title}>{item.title}</div>
                  <div className={styles.authors}>{item.authors}</div>
                </div>
              </div>
            </Link>
          </li>
        ))}
        <li />
      </ul>
      <button type='button' onClick={onPrevClick}>
        ì™„
      </button>
      <button type='button' onClick={test}>
        -&gt;
      </button>
    </div>
  )
}
