import { useEffect, useRef, useState } from 'react'
import { useQuery } from 'react-query'
import { Link } from 'react-router-dom'
import { getSearchListApi } from 'services/bookSearchApi'
import styles from './bookList.module.scss'

interface Props {
  search: string
  title: string
  next: boolean
}

interface Items {
  authors: string[]
  contents: string
  datetime: Date
  isbn: string
  price: number
  publisher: string
  sale_price: number
  status: string
  thumbnail: string
  title: string
  translators: any[]
  url: string
}

const BookList = ({ search, title, next }: Props) => {
  const [pageNum, setPageNum] = useState(1)
  const [itemList, setItemList] = useState<Array<Items>>([])
  const [isLoaded, setIsLoaded] = useState(false)
  const target = useRef<HTMLDivElement>(null)
  let dataCount: boolean

  // const { data } = useQuery(
  //   ['getSearchListApi', search, pageNum],
  //   () =>
  //     getSearchListApi(search, pageNum).then((res) => {
  //       setItemList((prev) => prev.concat(res.data.documents))
  //       dataCount = data.meta.is_end
  //       return res.data
  //     }),
  //   {
  //     enabled: !!search,
  //   }
  // )

  useEffect(() => {
    getSearchListApi(search, pageNum).then((res) => {
      setItemList((prev) => prev.concat(res.data.documents))
    })
  }, [])

  useEffect(() => {
    console.log(pageNum)
    console.log(itemList)
  }, [itemList, pageNum])

  useEffect(() => {
    let observer: IntersectionObserver
    const getMoreItem = async () => {
      if (!next) return
      setIsLoaded(true)
      // eslint-disable-next-line no-promise-executor-return
      await new Promise((resolve) => setTimeout(resolve, 3000))
      // if (!data.meta.is_end)
      setPageNum((prev) => prev + 1)
      setIsLoaded(false)
    }
    const handleObserve = async ([entry]: IntersectionObserverEntry[]) => {
      if (entry.isIntersecting) {
        observer.unobserve(entry.target)
        await getMoreItem()
        observer.observe(entry.target)
      }
    }
    if (target?.current) {
      observer = new IntersectionObserver(handleObserve, {
        threshold: 0.7,
      })
      observer.observe(target.current)
    }
    return () => observer && observer.disconnect()
  }, [next, target])

  const bookItems =
    itemList.length > 0 &&
    data.documents
      .filter((item: Items) => item.title !== title)
      .map((item: Items) => (
        <li key={item.isbn} className={styles.item}>
          <Link to={`../bookdetail/${item.publisher} ${item.title}`}>
            <img className={styles.bookImg} src={item.thumbnail} alt={`${item.title} book cover`} />
            <div className={styles.bookTitle}>{item.title}</div>
          </Link>
        </li>
      ))

  // const bookItems =
