import { VictoryAxis, VictoryChart, VictoryLine, VictoryTheme } from 'victory'

const data = [
  [
    { x: 1, y: 1 },
    { x: 2, y: 2 },
    { x: 3, y: 3 },
    { x: 4, y: 4 },
  ],
  [
    { x: 1, y: 75 },
    { x: 2, y: 85 },
    { x: 3, y: 95 },
    { x: 4, y: 100 },
  ],
]
// find maxima for normalizing data
const maxima = data.map((dataset) => Math.max(...dataset.map((d) => d.y)))

const xOffsets = [50, 950] // 세로 줄
const tickPadding = [0, -20] // 세로줄 눈금? 부분 숫자 패딩
const anchors = ['end', 'start'] // 세로줄 눈금? 부분 숫자 패딩
const colors = ['green', 'blue'] // 색

const Test = () => {
  return (
    <div>
      <VictoryChart
        theme={VictoryTheme.material}
        width={1000}
        height={400}
        domain={{ y: [0, 1] }}
        animate={{
          duration: 2000,
          onLoad: { duration: 1000 },
        }}
      >
        {/* 도메인  */}
        <VictoryAxis />
        {data.map((d, i) => (
          <VictoryAxis
            dependentAxis
            key={i}
            offsetX={xOffsets[i]}
            style={{
              axis: { stroke: colors[i] },
              ticks: { padding: tickPadding[i] },
              tickLabels: { fill: colors[i], textAnchor: anchors[i] },
            }}
            // Use normalized tickValues (0 - 1)
            tickValues={[0.25, 0.5, 0.75, 1]} // 간격?
            // Re-scale ticks by multiplying by correct maxima
            tickFormat={(t) => `${t * maxima[i]}임`}
          />
        ))}
        {data.map((d, i) => (
          <VictoryLine
            key={i}
            data={d}
            style={{ data: { stroke: colors[i] } }}
            // normalize data
            y={(datum) => {
              console.log(datum)
              return datum.y / maxima[i]
            }}
          />
        ))}
      </VictoryChart>
    </div>
  )
}

export default Test
