import { useQuery } from 'react-query'
import { Link } from 'react-router-dom'
import { getSearchListApi } from 'services/bookSearchApi'
import styles from './bookList.module.scss'

interface Props {
  search: string
  title: string
  next: boolean
}

interface Items {
  authors: string[]
  contents: string
  datetime: Date
  isbn: string
  price: number
  publisher: string
  sale_price: number
  status: string
  thumbnail: string
  title: string
  translators: any[]
  url: string
}

const BookList = ({ search, title, next }: Props) => {
  const { data } = useQuery(['getSearchListApi', search], () => getSearchListApi(search).then((res) => res.data), {
    enabled: !!search,
  })

  const ioCallback = (entries, io) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        io.unobserve(entry.target)
        loadingStart()
        setTimeout(() => {
          addNewContent()
          loadingFinish()
          observeLastItem(io, document.querySelectorAll('.card'))
        }, 2000)
      }
    })
  }

  const observeLastItem = (io, items) => {
    const lastItem = items[items.length - 1]
    io.observe(lastItem)
  }

  const io = new IntersectionObserver(ioCallback, { threshold: 0.7 })
  observeLastItem(io, items)
  // nohack.tistory.com/124 [lucid_dream:티스토리]

  const bookItems =
    data !== undefined &&
    data.documents
      .filter((item: Items) => item.title !== title)
      .map((item: Items) => (
        <li key={item.isbn} className={styles.item}>
          <Link to={`../bookdetail/${item.publisher} ${item.title}`}>
            <img className={styles.bookImg} src={item.thumbnail} alt={`${item.title} book cover`} />
            <div className={styles.bookTitle}>{item.title}</div>
          </Link>
        </li>
      ))

