import styles from './myPage.module.scss'
import TopNavBar from 'components/TopNavBar'
import { PersonIcon, HeartIcon, CartIcon, TruckIcon, SettingIcon } from 'assets/svgs'
import { MouseEvent } from 'react'
import { userInfoState } from 'states/userInfo'
import { useNavigate } from 'react-router-dom'
import { useRecoilValue } from 'recoil'
import { useRecoil } from 'hooks/state'
import { themeState } from 'states/system'

const User = () => {
  const userInfo = useRecoilValue(userInfoState)
  const navigate = useNavigate()
  const [theme, setTheme] = useRecoil(themeState)

  const handleClick = (e: MouseEvent<HTMLButtonElement>) => {
    const { value } = e.currentTarget.dataset
    navigate(`/${value}`)
  }

  const handleChangeTheme = () => {
    if (theme === 'light') setTheme('dark')
    else setTheme('light')
  }
  return (
    <div className={styles.myPage}>
      <TopNavBar title='마이페이지' />
      <div className={styles.myPageContent}>
        <div className={styles.wrapper}>
          <div className={styles.userPhoto}>
            <PersonIcon />
          </div>
        </div>
        <div className={styles.myPageDatail}>
          <div className={styles.userName}>
            {userInfo.userName} ({userInfo.userId})
          </div>
          <button type='button' onClick={handleChangeTheme}>
            버튼
          </button>
        </div>
      </div>
    </div>
  )
}

export default User
