import { useQuery } from 'react-query'
import { useParams } from 'react-router-dom'
import GNB from 'routes/_shared/GNB'
import { getSearchListApi } from 'services/bookSearchApi'
import styles from './bookDetail.module.scss'
import { MouseEvent } from 'react'

const BookDetail = () => {
  const { paramValue } = useParams()

  const { data, isLoading } = useQuery(
    ['getSearchListApi', paramValue],
    () => getSearchListApi(paramValue).then((res) => res.data),
    {
      enabled: !!paramValue,
    }
  )

  const handleClickBtn = (e: MouseEvent<HTMLButtonElement>) => {
    const { store } = e.currentTarget.dataset
    console.log(store)

    const storeData = store.get(store)
    // let storeData
    // if (id === 'likeStore') {
    //   storeData = store.get('likeStore')

    //   storeData
    //   if (!store.get('likeStore')) {
    //     store.set('likeStore', [])
    //   }
    // }
  }

  console.log(data)
  console.log(isLoading)
  return (
    <div className={styles.bookDetail}>
      <GNB />
      {!isLoading && data ? (
        <div>
          <div className={styles.detailWrapper}>
            <div>
              <img className='fit-picture' src={data.documents[0].thumbnail} alt={`${data.documents[0].title}_img`} />
            </div>
            <div>
              <div>{data.documents[0].title}</div>
              <div>{data.documents[0].authors} 저</div>
              <div>
                {data.documents[0].publisher} 출판 ({data.documents[0].datetime})
              </div>
              <div>판매가 {data.documents[0].sale_price}</div>
              <div>
                <button type='button' onClick={handleClickBtn} data-store='likeStore'>
                  찜
                </button>
                <button type='button' onClick={handleClickBtn} data-store='cartStore'>
                  장바구니
                </button>
                <button type='button' onClick={handleClickBtn} data-store='buyStore'>
                  구매
                </button>
              </div>
            </div>
          </div>
          <div>
            {data.documents[0].contents}...<a href={data.documents[0].url}>더보기</a>
          </div>
        </div>
      ) : (
        <div>sdfds</div>
      )}
    </div>
  )
}

export default BookDetail
