import { useRecoil } from 'hooks/state'
import { bookCardValue } from 'states/inputSearchValue'
import store from 'store'
import { RatingStructure } from 'types/searchStructure'
import styles from './makeCard.module.scss'

const MakeCard = () => {
  const [bookIsbn] = useRecoil(bookCardValue)
  const ratingData = store.get('ratingStore')
  const selectBook = ratingData.filter((item: RatingStructure) => item.isbn === bookIsbn)[0]

  return (
    <div className={styles.makeCard}>
      <div className={styles.cardSection}>
        <div className={styles.card}>
          {selectBook.length > 0 && selectBook.thumbnail !== '' ? (
            <img className={styles.bookImg} src={selectBook.thumbnail} alt={`${selectBook.title}_img`} />
          ) : (
            <img className={styles.bookImg} src={noImage} alt='no img' />
          )}
        </div>
      </div>
      <div className={styles.makeSection}>여기는 꾸미기</div>
    </div>
  )
}

export default MakeCard
