import { atom } from 'recoil'

export const inputValue = atom<string>({
  key: 'inputValue',
  default: '',
})

export const searchValue = atom<string>({
  key: 'searchValue',
  default: '',
})

export const searchListOpen = atom<string>({
  key: 'searchListOpen',
  default: 'false',
})

export const bookCardContents = atom<string>({
  key: 'bookCardContents',
  default: 'false',
})
