import { BookList } from 'components'
import TopNavBar from 'components/TopNavBar'
import { useRef, useState } from 'react'
import { useParams } from 'react-router-dom'
import styles from './searchResult.module.scss'

const SearchResult = () => {
  const { paramValue } = useParams()
  const target = useRef<HTMLDivElement>(null)
  const [isVisible, setIsVisible] = useState(false)

  if (paramValue === undefined) return <div>error</div>

  const handleObserve = ([entry]: IntersectionObserverEntry[], observer: IntersectionObserver) => {
    if (entry.isIntersecting) {
      console.log('dsad')
      setIsVisible(true)
      observer.unobserve(entry.target)
    }
  }

  let observer: IntersectionObserver

  if (target?.current) {
    const options = {
      root: target.current,
      threshold: 0.75,
    }
    observer = new IntersectionObserver(handleObserve, options)
    observer.observe(target.current)
  }

  return (
    <div className={styles.searchResult}>
      <TopNavBar title='검색결과' />
      <div className={styles.bookList}>
        <BookList search={paramValue} title='' next />
        <div ref={target}>여기는 끝</div>
      </div>
    </div>
  )
}

export default SearchResult
